// Generated by CoffeeScript 1.3.3

$(function() {
  return $("#uploads li a").each(function() {
    var url;
    url = new URLParser($(this).attr("href"));
    switch (url.type) {
      case "image":
        $(this).html("<img src=\"" + url.url + "\" class=\"item\">").fancybox();
        break;
      case "audio":
        $(this).replaceWith("<audio src=\"" + url.url + "\" controls=\"controls\" class=\"item\" preload=\"none\">");
        break;
      case "video":
        $(this).replaceWith("<video src=\"" + url.url + "\" controls=\"controls\" class=\"item\" preload=\"none\">");
        break;
      default:
        $(this).addClass("item");
    }
    return console.log(url.type);
  });
});
